def alphaPlusOne(s):
    char_list = list(s)

    # start from least significant position
    for idx, c in enumerate(reversed(char_list)):

        if not isCarryAfterPlusOne(c):
            # no carry, + 1 and break
            char_list[idx] = chr(ord(c) + 1)
            break
        else:
